from pydantic_ai.tool_calls import FunctionModel, ToolCallPart, TextPart

class MockToolCall(FunctionModel):
    def call_weather_forecast(self, date: str) -> ToolCallPart | TextPart:
        # Simulate tool call or return text
        if date == "tomorrow":
            return ToolCallPart(name="weather_forecast", arguments={"date": "tomorrow", "temp": "20C"})
        return TextPart(text="No forecast available for that date.")

# Example Usage:
agent = MockToolCall()
result = agent.call_weather_forecast("tomorrow")
print(result)
